package com.example.zwh.scs.Wallet.Alipay;import android.text.TextUtils;import java.util.Map;/** * @desc 对于Android平台而言是一个map结构体。里面有三个key， * 其中memo是描述信息(类型为字符串)； * result是处理结果(类型为json结构字符串)； * resultStatus是结果码(类型为字符串)。 * @autho Zhaopeng Yang * created on 2019/2/28 18:50 */public class PayResult {    private String resultStatus;    private String result;    private String memo;    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}